mapper.update:
UPDATE fin_account SET 	
	ac_type = #{acType},
	card_num = #{cardNum},
	ac_name = #{acName},
	amount = #{amount}
WHERE id = #{id}

update fin_account set
amount = amount + #{amount}
where id = #{id}
