mapper.select:
SELECT 

FROM fin_record a

WHERE a.id = #{id}

SELECT 

FROM fin_record a




		AND a.id = #{id}


		AND a.re_type = #{reType}


		AND a.bus_type = #{busType}


		AND a.amount = #{amount}


		AND a.description = #{description}


		AND a.in_id = #{inId}


		AND a.out_id = #{outId}


		AND a.note_date = #{noteDate}




		ORDER BY ${page.orderBy}

select

from fin_record



			DATE_FORMAT(note_date,'%Y-%m-%d')  = #{dateStr}


			DATE_FORMAT(note_date,'%Y-%m')  = #{dateStr}


			DATE_FORMAT(note_date,'%Y') =#{dateStr}


			DATE_FORMAT(note_date,'%Y-%m-%d')  = DATE_FORMAT(now(),'%Y-%m-%d')

SELECT a.id AS "id",
CASE WHEN a.in_id = #{id} THEN "收入" WHEN a.out_id = #{id} THEN "支出" ELSE "" END
CASE WHEN a.in_id = #{acName} THEN "收入" WHEN a.out_id = #{acName} THEN "支出" ELSE "" END
AS "reType", a.bus_type AS "busType", a.amount AS "amount", a.description AS "description", a.in_id AS "inId", a.out_id AS "outId", a.note_date AS "noteDate" FROM fin_record a
and a.in_id = #{id} or a.out_id = #{id}
and a.in_id = #{acName} or a.out_id = #{acName}

SELECT

FROM fin_record a



		and DATE_FORMAT(note_date,'%Y-%m-%d')    BETWEEN #{startDate} AND #{endDate}


		and a.bus_type = #{busType}


		and


				DATE_FORMAT(note_date,'%Y-%m-%d')  = #{dateStr}


				DATE_FORMAT(note_date,'%Y-%m')  = #{dateStr}


				DATE_FORMAT(note_date,'%Y')  =#{dateStr}


				DATE_FORMAT(note_date,'%Y-%m-%d')  = DATE_FORMAT(now(),'%Y-%m-%d')




		and a.in_id = #{id} or a.out_id = #{id}


		and a.in_id = #{acName} or a.out_id = #{acName}




		ORDER BY ${page.orderBy}


		ORDER  BY note_date DESC

SELECT

FROM fin_record a



		and DATE_FORMAT(note_date,'%Y-%m-%d')    BETWEEN #{startDate} AND #{endDate}




		ORDER BY ${page.orderBy}


		ORDER  BY note_date DESC

